pageFunction:
function pageFunction(context) {
    // called on every page the crawler visits, use it to extract data from it
    var $ = context.jQuery;
    var events = [];
    $('.event-list').find('.event-item').each(function(i) {
        var that = this;
        var evtDesc = $(that).find('.event-desc').text().replace(/Learn more/, '').trim();
        events.push({
            title: $(this).find('[itemprop=summary]').text().trim(),
            location: {
                name: $(this).find('.event-venuename > a').text().trim() || false,
                address: $(this).find('.event-where > dd').text().toLowerCase().trim() === 'join us!' ? false : $(this).find('.event-where > dd').text().replace(/\(map\)/, '').trim(),
                // $(this).find('.event-where > dd').text().toLowerCase().trim() === 'join us!' ? false : $(this).find('.event-where > dd > a').attr('href'),
                map_url: $(this).find('.event-where > dd').text().toLowerCase().trim() === 'join us!' ? false : $(this).find('.event-where > dd').text().toLowerCase().trim() === 'join us!' ? false : $(this).find('.event-where > dd > a').attr('href'),
            },
            description: evtDesc,
            datetime: $(this).find('[itemprop=startDate]').attr('datetime'),
            event_url: $(this).find('.rsvp-callout-rsvp').attr('href'),
            attendees_count: parseInt($(this).find('.rsvpCount > a > em').text().trim()),
            features: (function() {
                var cateringItems = /(catering|🍏|🍎|🍐|🍊|🍋|🍌|🍉|🍇|🍓|🍈|🍒|🍑|🍍|🍅|🍆|🌶|🌽|🍠|🍯|🍞|🧀|🍗|🍖|🍤|🍳|🍔|🍟|🌭|🍕|🍝|🌮|🌯|🍜|🍲|🍥|🍣|🍱|🍛|🍙|🍚|🍘|🍢|🍡|🍧|🍨|🍦|🍰|🎂|🍮|🍬|🍭|🍫|🍿|🍩|🍪)/;
                var cateringResult = cateringItems.exec(evtDesc);
                var features = [];
                if (/(lightning|⚡️)/.exec(evtDesc)) {
                    features.push('⚡️');
                }
                if (cateringResult) {
                    features.push(cateringResult[0])
                }
                return features;
            })()
        });
    });

    var results = events;

    return results;
}

interceptRequest:
function interceptRequest(context, newRequest) {
    // called whenever the crawler finds a link to a new page,
    // use it to override default behavior
    return newRequest;
}
